/* 이 위쪽은 임폴트하는 용도로 사용이 됨  */

import { useState } from "react";
//모듈즈안에있는것들은 경로없이 불러올수있음 .js는 생략된것.{}니까 익스폴트한거지. 함수야.

function App() { /* 이걸 컴포넌트라고 함 */



  // let fnClickHandler = () => {

  // }//fnClickHandler
  function fnClickHandler() {
    let [nember, setNumber] = useState(1) //함수인데 리액트만 사용하는 함수를 '훅스'라고 한다.
    //n이라는 스테잇 변수를 만드는데 그게 초기값이1 하나 바뀌면 연동된 모든것이 바뀐다. 



    // ++number
    // document.querySelector(`.output`).innerHTML = number 이건 의미가 없다...
  }//fnClickHandler
  //일반적인 함수를 사용할때는.내가만든 함수를 사용할때는 function()더 많이쓴다
  //내가 클릭할때는 이미 가상돔이 진짜로 된 이후야. 이 함수는 클릭을 했을때 실행돼. 실제가 되었다는 뜻이지. 그래서 클릭을 할수있는거잖아
  //상태관리. 변수가 하나 바뀌면 그게 들어간, 심지어 연관된 애들이 모두다 바뀐다. 그렇게 만들자 그게 상태관리야 그게 state. usestate


  return (
    <>
      <h1>1씩증가</h1>
      <button onClick={fnClickHandler}>증가</button>
      {/* <button onClick={()=>{fnClickHandler(number);fn2();fn3();}}>증가</button> 이거랑 위랑 같은거.이렇게 함수를 많이 호출할때는 이게 필요함*/}
      {/* 여기서는 저기 위에서 다큐먼트쿼리셀렉터못씀 밑에있는건 가상이니까 아직 만들어지지않은거. 나중에 버추얼돔이 만들어지고나면 사용가능  이건 청사진같은거야. 아직 만들어지지않은.  그래서 온클릭이라는 속성을 사용한다. */}
      <p className="output">현재값 : {number}</p>
      {/* 클래스는 사용불가 스크립트문법이라서. */}
    </>
  );
}

export default App;
