/* 이 위쪽은 임폴트하는 용도로 사용이 됨  */


function App() { /* 이걸 컴포넌트라고 함 */
  let number = 1


  // let fnClickHandler = () => {

  // }//fnClickHandler
  function fnClickHandler() {
    alert()
  }//fnClickHandler
  //일반적인 함수를 사용할때는.내가만든 함수를 사용할때는 function()더 많이쓴다


  return (
    <>
      <h1>1씩증가</h1>
      <button onClick={fnClickHandler}>증가</button>
      {/* <button onClick={()=>{fnClickHandler(number)}}>증가</button> 이거랑 위랑 같은거.이게 필요할때도 있다*/}
      {/* 여기서는 다큐먼트쿼리셀렉터못씀 밑에있는건 가상이니까 아직 만들어지지않은거. 나중에 버추얼돔이 만들어지고나면 사용가능  이건 청사진같은거야. 아직 만들어지지않은.  그래서 온클릭이라는 속성을 사용한다. */ }
      <p>현재값 : {number}</p>
    </>
  );
}

export default App;
